import React from "react";
import TodoInput from "./TodoInput";
import Colorbar from "./Colorbar";
import TodoList from "./TodoList";
import TodoSearch from "./TodoSearch";
import { TodoProvider, useTodoContext } from "./TodoProvider"; // useTodoContext 추가
import "../TodoApp.css";

export default function TodoApp1() {
  const { todos, colors } = useTodoContext(); // todos와 colors 상태 가져오기

  return (
    <div className="background">
      {todos &&
        colors && ( // todos와 colors가 모두 존재할 때 렌더링
          <>
            <TodoProvider>
              <TodoSearch />
              <h1 className="title">Todo App</h1>
              <TodoInput />
              <Colorbar />
              <TodoList />
            </TodoProvider>
          </>
        )}
    </div>
  );
}
